import axios from "axios";
import React, { useState } from "react";

const EpmloyerSignup = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    companyName: "",
    companyLocation: "",
    phoneNumber: "",
    email: "",
    password: "",
  });

  const [selectedSector, setSelectedSector] = useState("");
  const [errors, setErrors] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSectorChange = (e) => {
    setSelectedSector(e.target.value);
  };

  const validateForm = () => {
    errors = {};

    // Validate first name
    if (!formData.firstName.trim()) {
      errors.firstName = "First name is required";
    }

    // Validate last name
    if (!formData.lastName.trim()) {
      errors.lastName = "Last name is required";
    }

    // Validate company name
    if (!formData.companyName.trim()) {
      errors.companyName = "Company name is required";
    }

    // Validate company location
    if (!formData.companyLocation.trim()) {
      errors.companyLocation = "Company location is required";
    }

    // Validate phone number
    if (!formData.phoneNumber.trim()) {
      errors.phoneNumber = "Phone number is required";
    }

    // Validate email
    if (!formData.email.trim()) {
      errors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = "Invalid email address";
    }

    // Validate password
    if (!formData.password) {
      errors.password = "Password is required";
    } else if (formData.password.length < 6) {
      errors.password = "Password must be at least 6 characters long";
    }

    // Validate selected sector
    if (!selectedSector) {
      errors.sector = "Please select a sector";
    }

    setErrors(errors);

    // Return true if there are no errors, false otherwise
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      try {
        const completeFormData = { ...formData, sector: selectedSector };
        const response = await axios.post(
          "http://localhost:5000/api/employer/signup",
          completeFormData
        );
        console.log(response.data);
        // Handle successful sign-up, show a success message, redirect, etc.
      } catch (error) {
        console.error("Error signing up employer:", error);
        // Handle errors, display error messages, etc.
      }
    }
  };
  return (
    <section className="bg-white dark:bg-gray-900">
      <div className="flex justify-center min-h-screen">
        <div
          className="hidden bg-cover lg:block lg:w-2/5"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1494621930069-4fd4b2e24a11?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=715&q=80')",
          }}
        ></div>

        <div className="flex items-center w-full max-w-3xl p-8 mx-auto lg:px-12 lg:w-3/5">
          <div className="w-full">
            <h1 className="text-2xl font-semibold tracking-wider text-gray-800 capitalize dark:text-white">
              Get your free account now.
            </h1>

            <p className="mt-4 text-gray-500 dark:text-gray-400">
              Letâ€™s get you all set up so you can verify your personal account
              and begin setting up your profile.
            </p>
            <form
              onSubmit={handleSubmit}
              className="grid grid-cols-1 gap-6 mt-8 md:grid-cols-2"
            >
              <div>
                <label
                  htmlFor="firstName"
                  className="block mb-2 text-sm text-gray-600 dark:text-gray-200"
                >
                  First Name
                </label>
                <input
                  id="firstName"
                  name="firstName"
                  type="text"
                  value={formData.firstName}
                  onChange={handleChange}
                  placeholder="John"
                  className="block w-full px-5 py-3 mt-2 text-gray-700 placeholder-gray-400 bg-white border border-gray-200 rounded-lg dark:placeholder-gray-600 dark:bg-gray-900 dark:text-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-400 focus:ring-blue-400 focus:outline-none focus:ring focus:ring-opacity-40"
                />
              </div>

